export const lockSql = {
    // Book lock 함수들
    lockBook: async (isbn) => {
        try {
            const [checkResult] = await promisePool.query(
                'SELECT IS_FREE_LOCK(?) as is_free', 
                [`book_lock_${isbn}`]
            );
            
            if (!checkResult[0].is_free) {
                return false;
            }

            const [lockResult] = await promisePool.query(
                'SELECT GET_LOCK(?, 0) as success', 
                [`book_lock_${isbn}`]
